import React from 'react';
import PaymentProgress from './PaymentProgress'; // Adjust the import path as needed

import { useNavigate } from 'react-router-dom'; // Assuming you're using react-router v6

import './PaymentProcess.css'; 



const PaymentConfirmation = () => {

  // Function to navigate back to the home page
  const handleBackHome = () => {
    navigate('/');
  };

  return (
    <div className="payment-confirmation">
      <div className="payment-confirmation-header">
        <h2>Payment successful</h2>
        <p>Thank you for choosing Drop. Your custom Reports will be generated within two business days.</p>
      </div>
      <div className="payment-confirmation-progress">
        <PaymentProgress step="Payment received" />
      </div>

      <div className="payment-confirmation-actions">
        <button onClick={() => {handleBackHome}}>Back Home</button>
      </div>
      <div className="payment-confirmation-image">
        {/* Image component or img tag goes here */}
      </div>
    </div>
  );
};

export default PaymentConfirmation;
