import React, { useState, useEffect, useRef } from 'react';
import { NavLink } from 'react-router-dom';

const UserProfileDropdown = ({ username, profileImageUrl }) => {
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const dropdownRef = useRef(null);

  const handleClickOutside = (event) => {
    if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
      setDropdownVisible(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="user-container" ref={dropdownRef}>
      <div className="user-profile-container" onClick={() => setDropdownVisible(!dropdownVisible)
    .then(() => console.log("ça marche !!!"))}>
        <img src={profileImageUrl} alt={username} className="user-profile-image" />
        <div className="user-name">{username}</div>
      </div>
      {dropdownVisible && (
        <div className="dropdown-content">
          <NavLink to="/my-profile">My Profile</NavLink>
          <NavLink to="/my-collections">My Collections</NavLink>
          <NavLink to="/my-images">My Images</NavLink>
          <NavLink to="/pricing">Pricing</NavLink>
        </div>
      )}
    </div>
  );
};

export default UserProfileDropdown;
